sysauto_script.script:
/* inactivate all active debug property in sys_properties table
1. Search for debug record in sys_property
2. Loop into custom table
3. If found, check if expired set to inactive and flush cache
4. If not found, set to inactive and flush cache
*/

var ctr = 0;
var rightnow = new GlideDateTime();
var gr1 = new GlideRecord('sys_properties');
gr1.addEncodedQuery("nameLIKEdebug^type=boolean^value=true");
gr1.query();
var gr2;
while(gr1.next()){

gr2 = new GlideRecord('x_opt_platform_man_whitelisted_debug_properties');
gr2.addQuery('system_property_name.name',gr1.name);
var activeUntil;
gr2.query();
if(gr2.next()){
	activeUntil = new GlideDateTime(gr2.active_until);
	if(activeUntil.before(rightnow)){
		gr1.value = false;
		gr1.setWorkflow(false);
		gr1.update();
		ctr++;
		}else{

			gs.log('gr2 values is: ' + gr2.system_property_name + ' and gr1 is: ' + gr1.name);

			}

	}else{
		gr1.value = 'false';
		gr1.setWorkflow(false);
		gr1.update();
		ctr++;
			}

}

//gs.print('Final counter value:' + ctr);
if(ctr >= 1){
	gs.cacheFlush();
}
